// src/pages/PlaylistDetails.jsx
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  getPlaylistDetails,
  removeTrackFromPlaylist,
  reorderPlaylistTracks,
  deletePlaylist, // si vous l'avez implémenté
} from '../services/spotifyService';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { FaGripVertical } from 'react-icons/fa';  // Icône d’adhérence
import './PlaylistDetails.css';

const PlaylistDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [playlist, setPlaylist] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPlaylistData = async () => {
      setLoading(true);
      try {
        const data = await getPlaylistDetails(id);
        setPlaylist(data);
      } catch (error) {
        console.error("Erreur lors de la récupération de la playlist :", error);
      } finally {
        setLoading(false);
      }
    };
    fetchPlaylistData();
  }, [id]);

  // Suppression de la playlist
  const handleDeletePlaylist = async () => {
    try {
      await deletePlaylist(id);
      navigate('/');
    } catch (error) {
      console.error("Erreur lors de la suppression de la playlist :", error);
      alert("Impossible de supprimer la playlist.");
    }
  };

  // Suppression d’une piste
  const handleRemoveTrack = async (trackUri) => {
    if (!playlist) return;
    try {
      await removeTrackFromPlaylist(id, trackUri);
      setPlaylist((prev) => ({
        ...prev,
        tracks: {
          ...prev.tracks,
          items: prev.tracks.items.filter(item => item.track.uri !== trackUri),
        },
      }));
    } catch (error) {
      console.error("Erreur lors de la suppression de la piste :", error);
      alert("Impossible de retirer la piste.");
    }
  };

  // Gestion du drag'n'drop
  const onDragEnd = async (result) => {
    if (!result.destination) return;
    const sourceIndex = result.source.index;
    const destIndex = result.destination.index;
    if (sourceIndex === destIndex) return;

    if (!playlist) return;
    const newItems = Array.from(playlist.tracks.items);
    const [removed] = newItems.splice(sourceIndex, 1);
    newItems.splice(destIndex, 0, removed);

    setPlaylist((prev) => ({
      ...prev,
      tracks: {
        ...prev.tracks,
        items: newItems,
      },
    }));

    try {
      await reorderPlaylistTracks(id, sourceIndex, destIndex);
    } catch (error) {
      console.error("Erreur lors du réordonnancement :", error);
      alert("Impossible de réordonner les pistes.");
    }
  };

  if (loading) return <div className="playlist-details loading-state">Chargement...</div>;
  if (!playlist) return <div className="playlist-details error-state">Playlist non trouvée.</div>;

  // Calcul de la durée totale
  let totalDuration = 0;
  playlist.tracks.items.forEach(item => {
    if (item.track?.duration_ms) {
      totalDuration += item.track.duration_ms;
    }
  });

  const formatDuration = (ms) => {
    const minutes = Math.floor(ms / 60000);
    const seconds = Math.floor((ms % 60000) / 1000);
    return `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`;
  };
  const totalFormatted = formatDuration(totalDuration);

  return (
    <div className="playlist-details">
      <header className="playlist-details-header">
        <div className="playlist-info-left">
          {playlist.images?.length > 0 ? (
            <img
              src={playlist.images[0].url}
              alt={playlist.name}
              className="playlist-cover"
            />
          ) : (
            <img
              src="/default-playlist.png"
              alt={playlist.name}
              className="playlist-cover"
            />
          )}
        </div>
        <div className="playlist-info-right">
          <h2>{playlist.name}</h2>
          {playlist.description && <p className="playlist-description">{playlist.description}</p>}
          <p className="playlist-duration">
            Nombre de pistes : {playlist.tracks.items.length} <br />
            Durée totale : {totalFormatted}
          </p>
          <button className="delete-playlist-btn" onClick={handleDeletePlaylist}>
            Supprimer la playlist
          </button>
        </div>
      </header>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="tracks-droppable">
          {(provided) => (
            <ul
              className="tracks-list"
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              {playlist.tracks.items.map((item, index) => (
                <Draggable
                  key={item.track.id || item.track.uri}
                  draggableId={item.track.id || item.track.uri}
                  index={index}
                >
                  {(provided, snapshot) => (
                    <li
                      ref={provided.innerRef}
                      {...provided.draggableProps} // Le li reçoit draggableProps
                      className={`track-item ${snapshot.isDragging ? "dragging" : ""}`}
                    >
                      {/* Seule l’icône a dragHandleProps */}
                      <div className="drag-handle" {...provided.dragHandleProps}>
                        <FaGripVertical size={20} color="#ccc" />
                      </div>
                      <div className="track-info">
                        <span className="track-number">{index + 1}.</span>
                        <span className="track-name">{item.track.name}</span>
                        <span className="track-duration">
                          {formatDuration(item.track.duration_ms)}
                        </span>
                      </div>
                      <button
                        className="remove-track-btn"
                        onClick={() => handleRemoveTrack(item.track.uri)}
                      >
                        X
                      </button>
                    </li>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </ul>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default PlaylistDetails;
