import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getArtistAlbums } from '../services/spotifyService';
import AlbumDisplay from '../components/AlbumDisplay';
import AddAlbumToPlaylistModal from '../components/AddAlbumToPlaylistModal';

const ArtistPage = () => {
  const { id } = useParams();
  const [albums, setAlbums] = useState([]);
  const [selectedAlbum, setSelectedAlbum] = useState(null);
  const [includeLive, setIncludeLive] = useState(true);
  const [selectedAlbums, setSelectedAlbums] = useState([]);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    async function fetchAlbums() {
      const data = await getArtistAlbums(id, includeLive);
      setAlbums(data);
    }
    fetchAlbums();
  }, [id, includeLive]);

  const toggleSelectAlbum = (album) => {
    if (selectedAlbums.find(a => a.id === album.id)) {
      setSelectedAlbums(selectedAlbums.filter(a => a.id !== album.id));
    } else {
      setSelectedAlbums([...selectedAlbums, album]);
    }
  };

  if (selectedAlbum) {
    return <AlbumDisplay album={selectedAlbum} onBack={() => setSelectedAlbum(null)} />;
  }

  return (
    <div>
      <h1>Albums</h1>
      <label>
        <input 
          type="checkbox" 
          checked={includeLive} 
          onChange={() => setIncludeLive(!includeLive)} 
        />
        Inclure les albums live
      </label>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', marginTop: '20px', overflowY: 'auto', maxHeight: '600px' }}>
        {albums.map(album => (
          <div key={album.id} style={{ position: 'relative', cursor: 'pointer' }}>
            <img 
              src={album.images[0]?.url} 
              alt={album.name} 
              style={{ width: '150px' }} 
              onClick={() => setSelectedAlbum(album)} 
            />
            <div>{album.name}</div>
            <div>{album.release_date.substring(0,4)}</div>
            <input 
              type="checkbox" 
              checked={!!selectedAlbums.find(a => a.id === album.id)} 
              onChange={() => toggleSelectAlbum(album)}
              style={{ position: 'absolute', top: '5px', right: '5px' }}
            />
          </div>
        ))}
      </div>
      {selectedAlbums.length > 0 && (
        <div style={{ marginTop: '20px' }}>
          <button onClick={() => setShowModal(true)}>
            Ajouter les albums sélectionnés à une playlist
          </button>
        </div>
      )}
      {showModal && (
        <AddAlbumToPlaylistModal 
          selectedAlbums={selectedAlbums} 
          onClose={() => { setShowModal(false); setSelectedAlbums([]); }} 
        />
      )}
    </div>
  );
};

export default ArtistPage;
