// src/pages/SearchResults.jsx
import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import {
  searchArtists,
  searchAlbums,
  searchTracks
} from '../services/spotifyService';
import AddToPlaylistModal from '../components/AddToPlaylistModal';
import './SearchResults.css';

const SearchResults = () => {
  const [params] = useSearchParams();
  const query = params.get('q'); // On suppose qu’on passe la requête dans ?q=... via la barre de recherche
  const [artistResults, setArtistResults] = useState([]);
  const [albumResults, setAlbumResults] = useState([]);
  const [trackResults, setTrackResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedItems, setSelectedItems] = useState([]); // Contiendra des objets {type, data: {...}}
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const fetchAll = async () => {
      setLoading(true);
      try {
        const [artists, albums, tracks] = await Promise.all([
          searchArtists(query),
          searchAlbums(query),
          searchTracks(query)
        ]);
        setArtistResults(artists.slice(0, 5));
        setAlbumResults(albums.slice(0, 5));
        setTrackResults(tracks.slice(0, 5));
      } catch (error) {
        console.error('Erreur lors de la recherche :', error);
      } finally {
        setLoading(false);
      }
    };

    if (query) {
      fetchAll();
    }
  }, [query]);

  const toggleSelectItem = (type, item) => {
    const found = selectedItems.find(
      (x) => x.type === type && x.data.id === item.id
    );
    if (found) {
      setSelectedItems(selectedItems.filter((x) => !(x.type === type && x.data.id === item.id)));
    } else {
      setSelectedItems([...selectedItems, { type, data: item }]);
    }
  };

  return (
    <div className="search-results">
      <h2>Résultats pour "{query}"</h2>
      {loading && <p>Chargement...</p>}

      {!loading && (
        <>
          {/* Résultats Artistes */}
          {artistResults.length > 0 && (
            <section>
              <h3>Artistes</h3>
              <ul className="results-list">
                {artistResults.map((artist) => (
                  <li
                    key={artist.id}
                    onClick={() => toggleSelectItem('artist', artist)}
                    className={selectedItems.some((x) => x.type === 'artist' && x.data.id === artist.id)
                      ? 'selected'
                      : ''
                    }
                  >
                    <span className="result-title">{artist.name}</span>
                  </li>
                ))}
              </ul>
            </section>
          )}

          {/* Résultats Albums */}
          {albumResults.length > 0 && (
            <section>
              <h3>Albums</h3>
              <ul className="results-list">
                {albumResults.map((album) => (
                  <li
                    key={album.id}
                    onClick={() => toggleSelectItem('album', album)}
                    className={selectedItems.some((x) => x.type === 'album' && x.data.id === album.id)
                      ? 'selected'
                      : ''
                    }
                  >
                    <img
                      src={album.images[0]?.url}
                      alt={album.name}
                      className="result-thumbnail"
                    />
                    <span className="result-title">{album.name}</span>
                    <span className="result-subtitle">{album.release_date}</span>
                  </li>
                ))}
              </ul>
            </section>
          )}

          {/* Résultats Titres */}
          {trackResults.length > 0 && (
            <section>
              <h3>Titres</h3>
              <ul className="results-list">
                {trackResults.map((track) => (
                  <li
                    key={track.id}
                    onClick={() => toggleSelectItem('track', track)}
                    className={selectedItems.some((x) => x.type === 'track' && x.data.id === track.id)
                      ? 'selected'
                      : ''
                    }
                  >
                    <span className="result-title">{track.name}</span>
                    <span className="result-subtitle">{track.artists.map(a => a.name).join(', ')}</span>
                  </li>
                ))}
              </ul>
            </section>
          )}

          {selectedItems.length > 0 && (
            <div className="add-results-action">
              <button onClick={() => setShowModal(true)}>
                Ajouter les éléments sélectionnés à une playlist
              </button>
            </div>
          )}

          {showModal && (
            <AddToPlaylistModal
              items={selectedItems}
              onClose={() => {
                setShowModal(false);
                setSelectedItems([]);
              }}
            />
          )}
        </>
      )}
    </div>
  );
};

export default SearchResults;
