// src/components/AddAlbumToPlaylistModal.jsx
import React, { useState, useEffect } from 'react';
import { getUserPlaylists, addTracksToPlaylist, createPlaylist, getCurrentUser, getAlbumTracks } from '../services/spotifyService';

const AddAlbumToPlaylistModal = ({ selectedAlbums, onClose, onPlaylistCreated }) => {
  const [playlists, setPlaylists] = useState([]);
  const [selectedPlaylist, setSelectedPlaylist] = useState('');
  const [newPlaylistName, setNewPlaylistName] = useState('');

  useEffect(() => {
    async function fetchPlaylists() {
      const data = await getUserPlaylists();
      setPlaylists(data);
    }
    fetchPlaylists();
  }, []);

  const handleSubmit = async () => {
    try {
      let playlistId = selectedPlaylist;
      if (newPlaylistName) {
        const user = await getCurrentUser();
        const newPlaylist = await createPlaylist(user.id, newPlaylistName);
        playlistId = newPlaylist.id;
        // On informe le parent qu'une nouvelle playlist est créée
        if (onPlaylistCreated) {
          onPlaylistCreated(newPlaylist);
        }
      }
      let trackUris = [];
      for (const album of selectedAlbums) {
        const tracks = await getAlbumTracks(album.id);
        if (tracks && tracks.length > 0) {
          trackUris = trackUris.concat(tracks.map(track => track.uri));
        }
      }
      console.log("Track URIs à ajouter :", trackUris);
      if (trackUris.length > 0 && playlistId) {
        await addTracksToPlaylist(playlistId, trackUris);
        onClose();
      } else {
        alert("Aucun titre trouvé pour cet album ou playlist invalide.");
      }
    } catch (error) {
      console.error("Erreur lors de l'ajout des titres :", error);
      alert("Erreur lors de l'ajout des titres à la playlist.");
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3>Ajouter albums à une playlist</h3>
        <div>
          <h4>Choisissez une playlist existante :</h4>
          <select value={selectedPlaylist} onChange={(e) => setSelectedPlaylist(e.target.value)}>
            <option value="">Sélectionnez une playlist</option>
            {playlists.map(pl => (
              <option key={pl.id} value={pl.id}>{pl.name}</option>
            ))}
          </select>
        </div>
        <div>
          <h4>OU créez une nouvelle playlist :</h4>
          <input 
            type="text" 
            placeholder="Nom de la nouvelle playlist" 
            value={newPlaylistName} 
            onChange={(e) => setNewPlaylistName(e.target.value)}
          />
        </div>
        <div style={{ marginTop: '10px' }}>
          <button onClick={handleSubmit}>Ajouter</button>
          <button onClick={onClose}>Annuler</button>
        </div>
      </div>
    </div>
  );
};

export default AddAlbumToPlaylistModal;
