import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getPlaylistDetails, removeTrackFromPlaylist, reorderPlaylistTracks } from '../services/spotifyService';

const PlaylistDetails = () => {
  const { id } = useParams();
  const [playlist, setPlaylist] = useState(null);

  useEffect(() => {
    async function fetchPlaylistDetails() {
      const data = await getPlaylistDetails(id);
      setPlaylist(data);
    }
    fetchPlaylistDetails();
  }, [id]);

  const handleRemoveTrack = async (trackUri) => {
    await removeTrackFromPlaylist(id, trackUri);
    setPlaylist({
      ...playlist,
      tracks: {
        ...playlist.tracks,
        items: playlist.tracks.items.filter(item => item.track.uri !== trackUri)
      }
    });
  };

  const handleReorder = async (index, direction) => {
    if (!playlist) return;
    const tracks = [...playlist.tracks.items];
    const targetIndex = direction === 'up' ? index - 1 : index + 1;
    if (targetIndex < 0 || targetIndex >= tracks.length) return;
    
    // Pour simplifier, nous simulons le réordonnancement
    await reorderPlaylistTracks(id, index, targetIndex);
    const temp = tracks[index];
    tracks[index] = tracks[targetIndex];
    tracks[targetIndex] = temp;
    
    setPlaylist({
      ...playlist,
      tracks: {
        ...playlist.tracks,
        items: tracks
      }
    });
  };

  if (!playlist) return <div>Chargement...</div>;

  return (
    <div>
      <h2>{playlist.name}</h2>
      <ul>
        {playlist.tracks.items.map((item, index) => (
          <li key={item.track.id} style={{ marginBottom: '10px' }}>
            {item.track.name} 
            <button onClick={() => handleRemoveTrack(item.track.uri)}>Supprimer</button>
            <button onClick={() => handleReorder(index, 'up')}>Monter</button>
            <button onClick={() => handleReorder(index, 'down')}>Descendre</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PlaylistDetails;
