// src/pages/Home.jsx
import React, { useEffect } from 'react';
import { isAuthenticated, setTokenFromUrl, login } from '../services/authService';
import PlaylistPage from './PlaylistPage';
import './Home.css';

const Home = () => {
  useEffect(() => {
    // Extrait le token après redirection OAuth
    setTokenFromUrl();
  }, []);

  const authenticated = isAuthenticated();

  if (!authenticated) {
    return (
      <div className="home-container">
        <section className="hero">
          <div className="hero-content">
            <h1>Bienvenue sur MySpotifyApp</h1>
            <p>Connectez-vous pour découvrir vos playlists et explorer votre univers musical.</p>
            <button onClick={login} className="login-btn">Login</button>
          </div>
        </section>
      </div>
    );
  } else {
    return <PlaylistPage />;
  }
};

export default Home;
