// src/pages/SearchResults.jsx
import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { searchArtists, searchAlbums, searchTracks } from '../services/spotifyService';
import './SearchResults.css';

const SearchResults = () => {
  const [searchParams] = useSearchParams();
  const query = searchParams.get('q');
  const [artistResults, setArtistResults] = useState([]);
  const [albumResults, setAlbumResults] = useState([]);
  const [trackResults, setTrackResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAll = async () => {
      setLoading(true);
      try {
        const [artists, albums, tracks] = await Promise.all([
          searchArtists(query),
          searchAlbums(query),
          searchTracks(query)
        ]);
        setArtistResults(artists.slice(0, 5));
        setAlbumResults(albums.slice(0, 5));
        setTrackResults(tracks.slice(0, 5));
      } catch (error) {
        console.error('Erreur lors de la recherche :', error);
      } finally {
        setLoading(false);
      }
    };

    if (query) {
      fetchAll();
    }
  }, [query]);

  const handleArtistClick = (artist) => {
    // Navigue vers la page d'un artiste, ex. /artist/:id
    navigate(`/artist/${artist.id}`);
  };

  const handleAlbumClick = (album) => {
    // Navigue vers la page de l'album
    navigate(`/album/${album.id}`);
  };

  return (
    <div className="search-results">
      <h2>RÃ©sultats pour "{query}"</h2>
      {loading && <p>Chargement...</p>}

      {!loading && (
        <>
          {/* Section Artistes */}
          {artistResults.length > 0 && (
            <section className="results-section">
              <h3>Artistes</h3>
              <ul className="results-list">
                {artistResults.map((artist) => (
                  <li
                    key={artist.id}
                    onClick={() => handleArtistClick(artist)}
                    className="results-item"
                  >
                    <span className="result-title">{artist.name}</span>
                  </li>
                ))}
              </ul>
            </section>
          )}

          {/* Section Albums */}
          {albumResults.length > 0 && (
            <section className="results-section">
              <h3>Albums</h3>
              <ul className="results-list">
                {albumResults.map((album) => (
                  <li
                    key={album.id}
                    onClick={() => handleAlbumClick(album)}
                    className="results-item"
                  >
                    <img
                      src={album.images[0]?.url}
                      alt={album.name}
                      className="result-thumbnail"
                    />
                    <span className="result-title">{album.name}</span>
                    <span className="result-subtitle">{album.release_date}</span>
                  </li>
                ))}
              </ul>
            </section>
          )}

          {/* Section Titres */}
          {trackResults.length > 0 && (
            <section className="results-section">
              <h3>Titres</h3>
              <ul className="results-list">
                {trackResults.map((track) => (
                  <li key={track.id} className="results-item">
                    <span className="result-title">{track.name}</span>
                    <span className="result-subtitle">
                      {track.artists.map(a => a.name).join(', ')}
                    </span>
                  </li>
                ))}
              </ul>
            </section>
          )}
        </>
      )}
    </div>
  );
};

export default SearchResults;
