import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { isAuthenticated, login, logout } from '../services/authService';
import './Header.css';

const Header = () => {
  const navigate = useNavigate();
  const [searchType, setSearchType] = useState('artist'); // valeurs possibles: artist, track, album
  const [query, setQuery] = useState('');

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    // Par exemple, on navigue vers une page de résultats de recherche
    navigate(`/search?type=${searchType}&q=${encodeURIComponent(query)}`);
  };

  return (
    <header className="app-header">
      <div className="header-left">
        <div className="logo" onClick={() => navigate('/')}>
          <h1>MySpotifyApp</h1>
        </div>
        <nav className="header-nav">
          <a onClick={() => navigate('/playlists')} className="nav-link">
            Playlists
          </a>
        </nav>
      </div>
      <div className="header-center">
        <form className="search-form" onSubmit={handleSearchSubmit}>
          <select
            value={searchType}
            onChange={(e) => setSearchType(e.target.value)}
          >
            <option value="artist">Artistes</option>
            <option value="track">Titres</option>
            <option value="album">Albums</option>
          </select>
          <input
            type="text"
            placeholder="Rechercher..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
          <button type="submit">Rechercher</button>
        </form>
      </div>
      <div className="header-right">
        <button
          onClick={() => {
            if (isAuthenticated()) {
              logout();
            } else {
              login();
            }
          }}
        >
          {isAuthenticated() ? 'Logout' : 'Login'}
        </button>
      </div>
    </header>
  );
};

export default Header;
