// src/pages/AlbumDetails.jsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getAlbumDetails } from '../services/spotifyService';
import './AlbumDetails.css';

const AlbumDetails = () => {
  const { id } = useParams(); // identifiant de l'album
  const [album, setAlbum] = useState(null);
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAlbum = async () => {
      setLoading(true);
      try {
        const data = await getAlbumDetails(id);
        setAlbum(data);
        if (data.tracks && data.tracks.items) {
          setTracks(data.tracks.items);
        }
      } catch (error) {
        console.error("Erreur lors de la récupération de l'album :", error);
      } finally {
        setLoading(false);
      }
    };
    fetchAlbum();
  }, [id]);

  const formatDuration = (durationMs) => {
    const minutes = Math.floor(durationMs / 60000);
    const seconds = Math.floor((durationMs % 60000) / 1000);
    return `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`;
  };

  if (loading) {
    return <div className="album-details loading-state">Chargement...</div>;
  }

  if (!album) {
    return <div className="album-details error-state">Album non trouvé.</div>;
  }

  const totalDuration = tracks.reduce((sum, track) => sum + track.duration_ms, 0);

  return (
    <div className="album-details">
      <header className="album-header">
        <div className="album-cover">
          <img
            src={album.images[0]?.url || '/default-album.png'}
            alt={album.name}
          />
        </div>
        <div className="album-info">
          <h1 className="album-name">{album.name}</h1>
          <p className="album-artist">
            {album.artists.map((artist) => artist.name).join(', ')}
          </p>
          <p className="album-meta">
            {tracks.length} chansons – Durée totale : {formatDuration(totalDuration)}
          </p>
          {album.label && <p className="album-label">Label : {album.label}</p>}
          {/* Description : l'API Spotify ne fournit pas toujours de description,
              mais si vous disposez d'une propriété 'description', vous pouvez l'afficher : */}
          {album.description && (
            <p className="album-description">{album.description}</p>
          )}
        </div>
      </header>

      <section className="album-tracks">
        <h2 className="tracks-title">Liste des chansons</h2>
        <ul className="tracks-list">
          {tracks.map((track, index) => (
            <li key={track.id} className="track-item">
              <span className="track-index">{index + 1}.</span>
              <span className="track-name">{track.name}</span>
              <span className="track-duration">{formatDuration(track.duration_ms)}</span>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default AlbumDetails;
