// src/pages/PlaylistDetails.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  getPlaylistInfo,
  getAllPlaylistTracks,
  removeTrackFromPlaylist,
  reorderPlaylistTracks,
  deletePlaylist,
} from '../services/spotifyService';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { FaGripVertical, FaTimes } from 'react-icons/fa';
import './PlaylistDetails.css';

const PlaylistDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [playlistInfo, setPlaylistInfo] = useState(null); // nom, desc, images
  const [allTracks, setAllTracks] = useState([]);         // toutes les pistes
  const [displayCount, setDisplayCount] = useState(20);   // nb de pistes affichées initialement
  const [loadingInfo, setLoadingInfo] = useState(true);
  const [loadingTracks, setLoadingTracks] = useState(true);
  const [loadingDelete, setLoadingDelete] = useState(false);

  // Calcul durée totale
  const [totalDurationMs, setTotalDurationMs] = useState(0);

  // Scroll ref
  const containerRef = useRef(null);

  useEffect(() => {
    const loadInfo = async () => {
      setLoadingInfo(true);
      try {
        const info = await getPlaylistInfo(id);
        setPlaylistInfo(info);
      } catch (err) {
        console.error("Erreur getPlaylistInfo :", err);
      } finally {
        setLoadingInfo(false);
      }
    };
    loadInfo();

    const loadTracks = async () => {
      setLoadingTracks(true);
      try {
        const items = await getAllPlaylistTracks(id);
        setAllTracks(items);
        // Calcul durée totale
        let totalMs = 0;
        for (const it of items) {
          if (it.track?.duration_ms) {
            totalMs += it.track.duration_ms;
          }
        }
        setTotalDurationMs(totalMs);
      } catch (err) {
        console.error("Erreur getAllPlaylistTracks :", err);
      } finally {
        setLoadingTracks(false);
      }
    };
    loadTracks();
  }, [id]);

  const handleDeletePlaylist = async () => {
    if (loadingDelete) return;
    setLoadingDelete(true);
    try {
      await deletePlaylist(id);
      navigate('/');
    } catch (err) {
      console.error("Erreur deletePlaylist :", err);
      alert("Impossible de supprimer.");
    } finally {
      setLoadingDelete(false);
    }
  };

  const handleRemoveTrack = async (trackUri) => {
    try {
      await removeTrackFromPlaylist(id, trackUri);
      setAllTracks(prev => prev.filter(i => i.track.uri !== trackUri));
    } catch (error) {
      console.error("Erreur removeTrack :", error);
      alert("Impossible de retirer la piste.");
    }
  };

  // Drag & drop
  const onDragEnd = async (result) => {
    if (!result.destination) return;
    const sourceIndex = result.source.index;
    const destIndex = result.destination.index;
    if (sourceIndex === destIndex) return;

    const newAll = Array.from(allTracks);
    const [removed] = newAll.splice(sourceIndex, 1);
    newAll.splice(destIndex, 0, removed);

    setAllTracks(newAll);

    try {
      await reorderPlaylistTracks(id, sourceIndex, destIndex);
    } catch (err) {
      console.error("Erreur reorder :", err);
      alert("Impossible de réordonner.");
    }
  };

  // Local infinite scroll => On a allTracks déjà chargé
  // On augmente displayCount par 20 quand on scrolle vers le bas
  const handleScroll = () => {
    const container = containerRef.current;
    if (!container) return;
    const scrollTop = container.scrollTop;
    const scrollHeight = container.scrollHeight;
    const clientHeight = container.clientHeight;

    if (scrollTop + clientHeight >= scrollHeight - 100) {
      // Charger +20 si dispo
      if (displayCount < allTracks.length) {
        setDisplayCount(prev => prev + 20);
      }
    }
  };

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;
    container.addEventListener('scroll', handleScroll);
    return () => container.removeEventListener('scroll', handleScroll);
  }, [allTracks, displayCount]);

  // Format durée => heures:minutes:seconds si vous voulez
  const formatDuration = (ms) => {
    const hours = Math.floor(ms / 3600000);
    const restMs = ms % 3600000;
    const minutes = Math.floor(restMs / 60000);
    const seconds = Math.floor((restMs % 60000) / 1000);

    if (hours > 0) {
      return `${hours}h ${minutes}m ${seconds < 10 ? "0" : ""}${seconds}s`;
    } else {
      return `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`;
    }
  };

  // On affiche displayTracks = allTracks.slice(0, displayCount)
  const displayTracks = allTracks.slice(0, displayCount);

  // Rendu
  if (loadingInfo && !playlistInfo) {
    return <div className="playlist-details loading-state">Chargement infos...</div>;
  }
  if (!playlistInfo) {
    return <div className="playlist-details error-state">Playlist non trouvée.</div>;
  }

  return (
    <div className="playlist-details">
      <header className="playlist-details-header">
        <div className="playlist-info-left">
          {playlistInfo.images?.length > 0 ? (
            <img
              src={playlistInfo.images[0].url}
              alt={playlistInfo.name}
              className="playlist-cover"
            />
          ) : (
            <img
              src="/default-playlist.png"
              alt={playlistInfo.name}
              className="playlist-cover"
            />
          )}
        </div>
        <div className="playlist-info-right">
          <h2>{playlistInfo.name}</h2>
          {playlistInfo.description && <p className="playlist-description">{playlistInfo.description}</p>}
          <p className="playlist-duration">
            {allTracks.length} pistes – {formatDuration(totalDurationMs)}
          </p>
          <button
            className="delete-playlist-btn"
            onClick={handleDeletePlaylist}
            disabled={loadingDelete}
          >
            {loadingDelete ? "Suppression..." : "Supprimer la playlist"}
          </button>
        </div>
      </header>

      <div className="tracks-container" ref={containerRef}>
        {loadingTracks && allTracks.length === 0 && (
          <p style={{ textAlign: 'center' }}>Chargement pistes...</p>
        )}
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="tracks-droppable">
            {(provided) => (
              <ul className="tracks-list" ref={provided.innerRef} {...provided.droppableProps}>
                {displayTracks.map((item, index) => {
                  const track = item.track;
                  const artists = track.artists?.map(a => a.name).join(', ');
                  const albumName = track.album?.name;

                  return (
                    <Draggable
                      key={track.id || track.uri}
                      draggableId={track.id || track.uri}
                      index={index}
                    >
                      {(provided, snapshot) => (
                        <li
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          className={`track-item ${snapshot.isDragging ? "dragging" : ""}`}
                        >
                          <div className="drag-handle" {...provided.dragHandleProps}>
                            <FaGripVertical size={20} color="#ccc" />
                          </div>
                          <div className="track-content">
                            <div className="track-main-line">
                              <span className="track-number">{index + 1}.</span>
                              <span className="track-name">{track.name}</span>
                            </div>
                            <div className="track-subinfo">
                              {artists} – {albumName}
                            </div>
                          </div>
                          <div className="track-right">
                            <span className="track-duration">
                              {formatDuration(track.duration_ms)}
                            </span>
                            <button
                              className="remove-track-btn"
                              onClick={() => handleRemoveTrack(track.uri)}
                            >
                              <FaTimes size={16} color="#ff4b4b" />
                            </button>
                          </div>
                        </li>
                      )}
                    </Draggable>
                  );
                })}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
        </DragDropContext>
        {/* Indication s'il reste à afficher plus */}
        {displayCount < allTracks.length && (
          <p style={{ textAlign: 'center', color: '#999', marginTop: '8px' }}>
            Scrollez pour afficher plus...
          </p>
        )}
        {loadingTracks && allTracks.length > 0 && (
          <p style={{ textAlign: 'center', color: '#999' }}>Chargement...</p>
        )}
      </div>
    </div>
  );
};

export default PlaylistDetails;
