// src/pages/Home.jsx
import React, { useState, useEffect } from 'react';
import ArtistSearch from '../components/ArtistSearch';
import ArtistList from '../components/ArtistList';
import AuthButton from '../components/AuthButton';
import { setTokenFromUrl, isAuthenticated } from '../services/authService';
import { searchArtists, getUserPlaylists } from '../services/spotifyService';
import './Home.css';

const Home = () => {
  useEffect(() => {
    setTokenFromUrl();
  }, []);

  const [artists, setArtists] = useState([]);
  const [playlists, setPlaylists] = useState([]);

  useEffect(() => {
    if (isAuthenticated()) {
      // Si connecté, on récupère les playlists
      getUserPlaylists()
        .then(data => setPlaylists(data))
        .catch(error => console.error("Erreur lors de la récupération des playlists :", error));
    }
  }, []);

  const handleSearch = async (query) => {
    try {
      const results = await searchArtists(query);
      setArtists(results);
    } catch (error) {
      console.error("Erreur de recherche :", error);
    }
  };

  return (
    <div className="home-container">
      <section className="hero">
        <div className="hero-content">
          <h1 className="hero-title">Explorez votre univers musical</h1>
          <p className="hero-subtitle">
            Connectez-vous pour découvrir, gérer et créer vos playlists sur Spotify.
          </p>
          <AuthButton />
        </div>
      </section>
      {isAuthenticated() && playlists.length > 0 && (
        <section className="playlists-preview">
          <h2>Vos Playlists</h2>
          <div className="playlists-grid">
            {playlists.map(pl => (
              <div key={pl.id} className="playlist-card">
                <img src={pl.images[0]?.url || '/default-playlist.png'} alt={pl.name} />
                <div className="playlist-info">
                  <h3>{pl.name}</h3>
                  <p>{pl.tracks.total} chansons</p>
                </div>
              </div>
            ))}
          </div>
        </section>
      )}
      <section className="search-area">
        <ArtistSearch onSearch={handleSearch} />
      </section>
      {artists.length > 0 && (
        <section className="results-area">
          <h2>Artistes trouvés</h2>
          <ArtistList artists={artists} />
        </section>
      )}
    </div>
  );
};

export default Home;
