// src/components/Header.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { isAuthenticated, login, logout } from '../services/authService';
import './Header.css';

const Header = () => {
  const navigate = useNavigate();
  const [query, setQuery] = useState('');

  // Gère la soumission du formulaire de recherche unifiée
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (query.trim()) {
      navigate(`/search?q=${encodeURIComponent(query)}`);
      setQuery('');
    }
  };

  return (
    <header className="app-header">
      <div className="header-left">
        <div className="logo" onClick={() => navigate('/')}>
          <h1>MySpotifyApp</h1>
        </div>
        <nav className="header-nav">
          <span className="nav-link" onClick={() => navigate('/playlists')}>
            Playlists
          </span>
        </nav>
      </div>

      <div className="header-center">
        <form className="search-form" onSubmit={handleSearchSubmit}>
          <input
            type="text"
            placeholder="Rechercher artistes, albums, titres..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
          <button type="submit">Rechercher</button>
        </form>
      </div>

      <div className="header-right">
        <button
          onClick={() => {
            if (isAuthenticated()) {
              logout();
            } else {
              login();
            }
          }}
        >
          {isAuthenticated() ? 'Logout' : 'Login'}
        </button>
      </div>
    </header>
  );
};

export default Header;
