// src/pages/SearchResults.jsx
import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useSearchParams } from 'react-router-dom';
import axios from 'axios'; // Pour charger la page "next" de l'API
import {
  searchArtists,
  searchAlbums,
  searchTracks
} from '../services/spotifyService';
import './SearchResults.css';

const TABS = {
  ARTIST: 'artist',
  ALBUM: 'album',
  TRACK: 'track',
};

const SearchResults = () => {
  const [searchParams] = useSearchParams();
  const query = searchParams.get('q') || '';

  // Onglet actif : "artist" (par défaut), "album", ou "track"
  const [activeTab, setActiveTab] = useState(TABS.ARTIST);

  // États pour les artistes
  const [artistItems, setArtistItems] = useState([]);
  const [artistNext, setArtistNext] = useState(null);

  // États pour les albums
  const [albumItems, setAlbumItems] = useState([]);
  const [albumNext, setAlbumNext] = useState(null);

  // États pour les titres
  const [trackItems, setTrackItems] = useState([]);
  const [trackNext, setTrackNext] = useState(null);

  // Indique si on charge la page suivante
  const [loadingMore, setLoadingMore] = useState(false);

  // Contrôle du chargement initial
  const [loadingInitial, setLoadingInitial] = useState(true);

  // Réf pour le container scrollable
  const containerRef = useRef(null);

  // ===================================================================
  // Fonctions pour charger la 1ère page de résultats
  // ===================================================================
  const loadInitialData = useCallback(async () => {
    if (!query) return;

    setLoadingInitial(true);

    try {
      // On appelle nos fonctions search* (limite 20)
      // On pourrait vouloir plus de 5 ou 10. Ajustez selon vos besoins.
      const [artists, albums, tracks] = await Promise.all([
        searchArtists(query),
        searchAlbums(query),
        searchTracks(query),
      ]);

      // Spotify renvoie habituellement un objet
      // On gère l'URL de la page suivante si l'API la fournit
      setArtistItems(artists.items);
      setArtistNext(artists.next); // ex: "https://api.spotify.com/v1/search?..."

      setAlbumItems(albums.items);
      setAlbumNext(albums.next);

      setTrackItems(tracks.items);
      setTrackNext(tracks.next);

    } catch (error) {
      console.error("Erreur lors de la recherche initiale :", error);
    } finally {
      setLoadingInitial(false);
    }
  }, [query]);

  useEffect(() => {
    // Recharge la data à chaque fois que la query change
    if (query) {
      loadInitialData();
    }
  }, [query, loadInitialData]);

  // ===================================================================
  // Fonctions pour charger la page suivante (scroll infini)
  // ===================================================================
  const fetchMoreData = async (type) => {
    try {
      setLoadingMore(true);
      let nextURL = null;
      if (type === TABS.ARTIST && artistNext) nextURL = artistNext;
      if (type === TABS.ALBUM && albumNext) nextURL = albumNext;
      if (type === TABS.TRACK && trackNext) nextURL = trackNext;

      if (!nextURL) return; // aucune page suivante

      const response = await axios.get(nextURL); // charger la page "next"
      const data = response.data;

      if (type === TABS.ARTIST) {
        setArtistItems(prev => [...prev, ...data.artists.items]);
        setArtistNext(data.artists.next); // maj du next
      } else if (type === TABS.ALBUM) {
        setAlbumItems(prev => [...prev, ...data.albums.items]);
        setAlbumNext(data.albums.next);
      } else if (type === TABS.TRACK) {
        setTrackItems(prev => [...prev, ...data.tracks.items]);
        setTrackNext(data.tracks.next);
      }
    } catch (error) {
      console.error("Erreur lors du chargement de la page suivante :", error);
    } finally {
      setLoadingMore(false);
    }
  };

  // ===================================================================
  // Gestion du scroll infini
  // ===================================================================
  // On surveille quand on atteint la fin de la liste pour l'onglet actif
  const handleScroll = () => {
    if (loadingMore) return;

    const container = containerRef.current;
    if (!container) return;

    const scrollTop = container.scrollTop;
    const scrollHeight = container.scrollHeight;
    const clientHeight = container.clientHeight;

    // Si on est proche du bas de la liste (ex. 100px avant la fin)
    if (scrollTop + clientHeight >= scrollHeight - 100) {
      // Charger la page suivante pour l'onglet actif
      fetchMoreData(activeTab);
    }
  };

  // Attacher l'événement scroll
  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    container.addEventListener('scroll', handleScroll);
    return () => container.removeEventListener('scroll', handleScroll);
  }, [activeTab, handleScroll, loadingMore]);

  // ===================================================================
  // Rendu de chaque onglet
  // ===================================================================
  const renderArtists = () => {
    return (
      <ul className="results-list">
        {artistItems.map((artist) => (
          <li key={artist.id} className="results-item">
            <span className="result-title">{artist.name}</span>
            {/* Ajoutez un onClick si vous voulez naviguer vers /artist/:id */}
          </li>
        ))}
      </ul>
    );
  };

  const renderAlbums = () => {
    return (
      <ul className="results-list">
        {albumItems.map((album) => (
          <li key={album.id} className="results-item">
            <img
              src={album.images[0]?.url}
              alt={album.name}
              className="result-thumbnail"
            />
            <span className="result-title">{album.name}</span>
            <span className="result-subtitle">{album.release_date}</span>
            {/* Ajoutez un onClick si vous voulez naviguer vers /album/:id */}
          </li>
        ))}
      </ul>
    );
  };

  const renderTracks = () => {
    return (
      <ul className="results-list">
        {trackItems.map((track) => (
          <li key={track.id} className="results-item">
            <span className="result-title">{track.name}</span>
            <span className="result-subtitle">
              {track.artists.map(a => a.name).join(', ')}
            </span>
            {/* Ajoutez un onClick si vous voulez faire qch sur le titre */}
          </li>
        ))}
      </ul>
    );
  };

  // ===================================================================
  // Composant principal
  // ===================================================================
  return (
    <div className="search-results-container">
      <h2>Résultats pour "{query}"</h2>
      {loadingInitial && <p>Chargement initial...</p>}

      {/* Tabs */}
      <div className="tabs-menu">
        <button
          className={activeTab === TABS.ARTIST ? 'tab-btn active' : 'tab-btn'}
          onClick={() => setActiveTab(TABS.ARTIST)}
        >
          Artistes
        </button>
        <button
          className={activeTab === TABS.ALBUM ? 'tab-btn active' : 'tab-btn'}
          onClick={() => setActiveTab(TABS.ALBUM)}
        >
          Albums
        </button>
        <button
          className={activeTab === TABS.TRACK ? 'tab-btn active' : 'tab-btn'}
          onClick={() => setActiveTab(TABS.TRACK)}
        >
          Titres
        </button>
      </div>

      {/* Container scrollable */}
      <div className="results-scrollable" ref={containerRef}>
        {!loadingInitial && activeTab === TABS.ARTIST && renderArtists()}
        {!loadingInitial && activeTab === TABS.ALBUM && renderAlbums()}
        {!loadingInitial && activeTab === TABS.TRACK && renderTracks()}

        {loadingMore && <p style={{ textAlign: 'center' }}>Chargement en plus...</p>}
      </div>
    </div>
  );
};

export default SearchResults;
