import React, { useState, useEffect } from 'react';
import ArtistSearch from '../components/ArtistSearch';
import ArtistList from '../components/ArtistList';
import AuthButton from '../components/AuthButton';
import { setTokenFromUrl } from '../services/authService';
import { searchArtists } from '../services/spotifyService';
import './Home.css';

const Home = () => {
  useEffect(() => {
    // Extraction du token après authentification OAuth
    setTokenFromUrl();
  }, []);

  const [artists, setArtists] = useState([]);

  const handleSearch = async (query) => {
    try {
      const results = await searchArtists(query);
      setArtists(results);
    } catch (error) {
      console.error("Erreur de recherche :", error);
    }
  };

  return (
    <div className="home-container">
      <section className="hero">
        <div className="hero-content">
          <h1 className="hero-title">Explorez votre univers musical</h1>
          <p className="hero-subtitle">
            Connectez-vous pour découvrir, gérer et créer vos playlists sur Spotify.
          </p>
          <AuthButton />
        </div>
      </section>
      <section className="search-area">
        <ArtistSearch onSearch={handleSearch} />
      </section>
      {artists.length > 0 && (
        <section className="results-area">
          <h2>Artistes trouvés</h2>
          <ArtistList artists={artists} />
        </section>
      )}
    </div>
  );
};

export default Home;
