// src/pages/PlaylistDetails.jsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getPlaylistDetails, removeTrackFromPlaylist, reorderPlaylistTracks } from '../services/spotifyService';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { FaGripVertical } from 'react-icons/fa';
import './PlaylistDetails.css';

const PlaylistDetails = () => {
  const { id } = useParams();
  const [playlist, setPlaylist] = useState(null);
  const [loading, setLoading] = useState(true);

  // Charge la playlist une seule fois et attribue des uniqueId stables à chaque piste
  useEffect(() => {
    const loadPlaylist = async () => {
      try {
        const data = await getPlaylistDetails(id);
        // Génère des uniqueId stables en utilisant track.uri et l'index de chargement initial
        const itemsWithUniqueIds = data.tracks.items.map((item, index) => ({
          ...item,
          uniqueId: `${item.track.uri}-${index}`,
        }));
        data.tracks.items = itemsWithUniqueIds;
        setPlaylist(data);
        setLoading(false);
      } catch (error) {
        console.error("Erreur lors de la récupération de la playlist :", error);
        setLoading(false);
      }
    };
    loadPlaylist();
  }, [id]);

  // Mise à jour locale après suppression : on retire l'élément sans régénérer les uniqueId
  const handleRemoveTrack = async (trackUri, uniqueId) => {
    try {
      await removeTrackFromPlaylist(id, trackUri);
      setPlaylist(prev => ({
        ...prev,
        tracks: {
          ...prev.tracks,
          items: prev.tracks.items.filter(item => item.uniqueId !== uniqueId),
        },
      }));
    } catch (error) {
      console.error("Erreur lors de la suppression de la piste :", error);
      alert("Erreur lors de la suppression de la piste.");
    }
  };

  // Gestion du drag'n'drop : mise à jour de l'ordre local sans recalculer les uniqueId
  const onDragEnd = async (result) => {
    if (!result.destination) return;
    const sourceIndex = result.source.index;
    const destIndex = result.destination.index;
    if (sourceIndex === destIndex) return;

    const newItems = Array.from(playlist.tracks.items);
    const [movedItem] = newItems.splice(sourceIndex, 1);
    newItems.splice(destIndex, 0, movedItem);

    setPlaylist(prev => ({
      ...prev,
      tracks: {
        ...prev.tracks,
        items: newItems,
      },
    }));

    try {
      await reorderPlaylistTracks(id, sourceIndex, destIndex);
    } catch (error) {
      console.error("Erreur lors du réordonnancement :", error);
      alert("Erreur lors du réordonnancement des pistes.");
    }
  };

  if (loading) return <div className="loading">Chargement...</div>;
  if (!playlist) return <div className="error">Playlist non trouvée.</div>;

  return (
    <div className="playlist-details">
      <header className="playlist-details-header">
        <h2>{playlist.name}</h2>
        {playlist.description && <p>{playlist.description}</p>}
      </header>
      <div className="tracks-container">
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="tracks-droppable">
            {(provided) => (
              <ul className="tracks-list" ref={provided.innerRef} {...provided.droppableProps}>
                {playlist.tracks.items.map((item, index) => (
                  <Draggable key={item.uniqueId} draggableId={item.uniqueId} index={index}>
                    {(provided, snapshot) => (
                      <li
                        ref={provided.innerRef}
                        {...provided.draggableProps} // Le li reçoit les draggableProps
                        className={`track-item ${snapshot.isDragging ? "dragging" : ""}`}
                        style={{ ...provided.draggableProps.style }}
                      >
                        {/* Seule la poignée reçoit les dragHandleProps */}
                        <div className="drag-handle" {...provided.dragHandleProps}>
                          <FaGripVertical size={20} color="#ccc" />
                        </div>
                        <div className="track-info">
                          <span className="track-number">{index + 1}.</span>
                          <span className="track-name">{item.track.name}</span>
                          <span className="track-duration">{formatDuration(item.track.duration_ms)}</span>
                        </div>
                        <button
                          className="remove-track-btn"
                          onClick={() => handleRemoveTrack(item.track.uri, item.uniqueId)}
                        >
                          Supprimer
                        </button>
                      </li>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
        </DragDropContext>
      </div>
    </div>
  );
};

function formatDuration(durationMs) {
  const minutes = Math.floor(durationMs / 60000);
  const seconds = Math.floor((durationMs % 60000) / 1000);
  return `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`;
}

export default PlaylistDetails;
