// src/pages/AlbumDetails.jsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getAlbumDetails } from '../services/spotifyService';
import AddAlbumToPlaylistModal from '../components/AddAlbumToPlaylistModal';
import './AlbumDetails.css';

const AlbumDetails = () => {
  const { id } = useParams(); // id de l'album
  const [album, setAlbum] = useState(null);
  const [tracks, setTracks] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAlbum = async () => {
      try {
        const data = await getAlbumDetails(id);
        setAlbum(data);
        setTracks(data.tracks.items);
        setLoading(false);
      } catch (error) {
        console.error("Erreur lors de la récupération de l'album :", error);
        setLoading(false);
      }
    };
    fetchAlbum();
  }, [id]);

  const totalDuration = tracks.reduce((acc, track) => acc + track.duration_ms, 0);

  const formatDuration = (durationMs) => {
    const minutes = Math.floor(durationMs / 60000);
    const seconds = Math.floor((durationMs % 60000) / 1000);
    return `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`;
  };

  if (loading) return <div className="loading">Chargement...</div>;
  if (!album) return <div className="error">Album non trouvé.</div>;

  return (
    <div className="album-details">
      <header className="album-header">
        <h2>{album.name}</h2>
        <p>{album.artists.map(a => a.name).join(', ')}</p>
        <p>{tracks.length} chansons - Durée totale : {formatDuration(totalDuration)}</p>
      </header>
      <div className="album-tracks">
        <h3>Liste des chansons</h3>
        <ul>
          {tracks.map((track, index) => (
            <li key={track.id}>
              {index + 1}. {track.name} ({formatDuration(track.duration_ms)})
            </li>
          ))}
        </ul>
      </div>
      <div className="add-album-action">
        <button onClick={() => setShowModal(true)}>
          Ajouter cet album à une playlist
        </button>
      </div>
      {showModal && (
        <AddAlbumToPlaylistModal
          selectedAlbums={[album]}
          onClose={() => setShowModal(false)}
        />
      )}
    </div>
  );
};

export default AlbumDetails;
