// src/pages/PlaylistDetails.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  getPlaylistInfo,
  getPlaylistTracks,
  removeTrackFromPlaylist,
  reorderPlaylistTracks,
  deletePlaylist,
} from '../services/spotifyService';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { FaGripVertical, FaTimes } from 'react-icons/fa';
import './PlaylistDetails.css';

const PlaylistDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  // Infos de base (nom, description, images)
  const [playlistInfo, setPlaylistInfo] = useState(null);

  // Pistes accumulées (scroll infini)
  const [tracks, setTracks] = useState([]);
  const [nextOffset, setNextOffset] = useState(0);
  const [total, setTotal] = useState(0); // nb total de pistes

  // States
  const [loadingInfo, setLoadingInfo] = useState(true);
  const [loadingTracks, setLoadingTracks] = useState(false);
  const [loadingDelete, setLoadingDelete] = useState(false);

  // Ref pour la div scrollable
  const containerRef = useRef(null);

  useEffect(() => {
    // Charger les infos de base
    const fetchInfo = async () => {
      setLoadingInfo(true);
      try {
        const info = await getPlaylistInfo(id);
        setPlaylistInfo(info);
      } catch (error) {
        console.error("Erreur getPlaylistInfo :", error);
      } finally {
        setLoadingInfo(false);
      }
    };
    fetchInfo();
  }, [id]);

  useEffect(() => {
    // Charger le premier lot de pistes
    loadMoreTracks(true);
  }, [id]);

  // Fonction pour charger plus de pistes
  const loadMoreTracks = async (reset = false) => {
    if (loadingTracks) return; // évite les doublons
    setLoadingTracks(true);

    try {
      // Si reset, on repart de offset=0
      const offset = reset ? 0 : nextOffset;
      const data = await getPlaylistTracks(id, offset, 100);
      // data.items = tableau de pistes, data.next, data.total, etc.

      if (reset) {
        setTracks(data.items || []);
      } else {
        setTracks((prev) => [...prev, ...data.items]);
      }
      setTotal(data.total || 0);

      // Calcul du new offset
      const newOffset = offset + (data.items?.length || 0);
      setNextOffset(newOffset);
      
      // Si data.items.length < 100, c’est qu’on a tout
    } catch (err) {
      console.error("Erreur getPlaylistTracks :", err);
    } finally {
      setLoadingTracks(false);
    }
  };

  // Détection du scroll => charger plus
  const handleScroll = () => {
    if (loadingTracks) return;
    const container = containerRef.current;
    if (!container) return;

    const scrollTop = container.scrollTop;
    const scrollHeight = container.scrollHeight;
    const clientHeight = container.clientHeight;

    // ~100px avant la fin
    if (scrollTop + clientHeight >= scrollHeight - 100) {
      // Charger plus s’il reste des pistes
      if (tracks.length < total) {
        loadMoreTracks(false);
      }
    }
  };

  useEffect(() => {
    // Attacher l’événement scroll au container
    const container = containerRef.current;
    if (!container) return;
    container.addEventListener('scroll', handleScroll);
    return () => container.removeEventListener('scroll', handleScroll);
  }, [tracks, total, loadingTracks]);

  // Suppression playlist
  const handleDeletePlaylist = async () => {
    if (loadingDelete) return;
    setLoadingDelete(true);
    try {
      await deletePlaylist(id);
      navigate('/');
    } catch (error) {
      console.error("Erreur lors de la suppression :", error);
      alert("Impossible de supprimer la playlist.");
    } finally {
      setLoadingDelete(false);
    }
  };

  // remove track
  const handleRemoveTrack = async (trackUri) => {
    try {
      await removeTrackFromPlaylist(id, trackUri);
      setTracks((prev) => prev.filter(t => t.track.uri !== trackUri));
    } catch (error) {
      console.error("Erreur removeTrack :", error);
      alert("Impossible de retirer la piste.");
    }
  };

  // Drag & drop
  const onDragEnd = async (result) => {
    if (!result.destination) return;
    const sourceIndex = result.source.index;
    const destIndex = result.destination.index;
    if (sourceIndex === destIndex) return;

    const newTracks = Array.from(tracks);
    const [removed] = newTracks.splice(sourceIndex, 1);
    newTracks.splice(destIndex, 0, removed);

    setTracks(newTracks);

    try {
      await reorderPlaylistTracks(id, sourceIndex, destIndex);
    } catch (error) {
      console.error("Erreur reorderPlaylistTracks :", error);
      alert("Impossible de réordonner les pistes.");
    }
  };

  // Calcul de la durée (on somme track.duration_ms)
  const formatDuration = (ms) => {
    const minutes = Math.floor(ms / 60000);
    const seconds = Math.floor((ms % 60000) / 1000);
    return `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`;
  };
  const totalDuration = tracks.reduce((sum, t) => sum + (t.track?.duration_ms || 0), 0);
  const totalFormatted = formatDuration(totalDuration);

  // rendering
  if (loadingInfo && !playlistInfo) {
    return <div className="playlist-details loading-state">Chargement infos...</div>;
  }

  if (!playlistInfo) {
    return <div className="playlist-details error-state">Playlist non trouvée.</div>;
  }

  return (
    <div className="playlist-details">
      <header className="playlist-details-header">
        <div className="playlist-info-left">
          {playlistInfo.images?.length > 0 ? (
            <img
              src={playlistInfo.images[0].url}
              alt={playlistInfo.name}
              className="playlist-cover"
            />
          ) : (
            <img
              src="/default-playlist.png"
              alt={playlistInfo.name}
              className="playlist-cover"
            />
          )}
        </div>
        <div className="playlist-info-right">
          <h2>{playlistInfo.name}</h2>
          {playlistInfo.description && <p className="playlist-description">{playlistInfo.description}</p>}
          <p className="playlist-duration">
            {tracks.length} pistes / {totalFormatted}
          </p>
          <button className="delete-playlist-btn" onClick={handleDeletePlaylist} disabled={loadingDelete}>
            {loadingDelete ? "Suppression..." : "Supprimer la playlist"}
          </button>
        </div>
      </header>

      {/* Container scrollable pour la liste de pistes */}
      <div className="tracks-container" ref={containerRef}>
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="tracks-droppable">
            {(provided) => (
              <ul className="tracks-list" ref={provided.innerRef} {...provided.droppableProps}>
                {tracks.map((item, index) => {
                  const track = item.track;
                  const artists = track.artists?.map(a => a.name).join(', ');
                  const albumName = track.album?.name;

                  return (
                    <Draggable
                      key={track.id || track.uri}
                      draggableId={track.id || track.uri}
                      index={index}
                    >
                      {(provided, snapshot) => (
                        <li
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          className={`track-item ${snapshot.isDragging ? "dragging" : ""}`}
                        >
                          {/* Seule l’icône a dragHandleProps */}
                          <div className="drag-handle" {...provided.dragHandleProps}>
                            <FaGripVertical size={20} color="#ccc" />
                          </div>
                          <div className="track-content">
                            <div className="track-main-line">
                              <span className="track-number">{index + 1}.</span>
                              <span className="track-name">{track.name}</span>
                            </div>
                            <div className="track-subinfo">
                              {artists} – {albumName}
                            </div>
                          </div>
                          <div className="track-right">
                            <span className="track-duration">
                              {formatDuration(track.duration_ms)}
                            </span>
                            <button
                              className="remove-track-btn"
                              onClick={() => handleRemoveTrack(track.uri)}
                            >
                              <FaTimes size={16} color="#ff4b4b" />
                            </button>
                          </div>
                        </li>
                      )}
                    </Draggable>
                  );
                })}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
        </DragDropContext>
        {loadingTracks && <p style={{ textAlign: 'center', color: '#aaa' }}>Chargement...</p>}
      </div>
    </div>
  );
};

export default PlaylistDetails;
