// src/pages/SearchResults.jsx
import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { searchTracks, searchAlbums } from '../services/spotifyService';
import AddTracksToPlaylistModal from '../components/AddTracksToPlaylistModal';
import AddAlbumToPlaylistModal from '../components/AddAlbumToPlaylistModal';
import './SearchResults.css';

const SearchResults = () => {
  const [searchParams] = useSearchParams();
  const type = searchParams.get('type'); // "track" ou "album"
  const query = searchParams.get('q');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedItems, setSelectedItems] = useState([]);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const fetchResults = async () => {
      setLoading(true);
      try {
        if (type === 'track') {
          const items = await searchTracks(query);
          setResults(items.slice(0, 10)); // affiche les 10 premiers résultats
        } else if (type === 'album') {
          const items = await searchAlbums(query);
          setResults(items.slice(0, 10));
        } else {
          setResults([]);
        }
      } catch (err) {
        console.error("Erreur lors de la recherche :", err);
      } finally {
        setLoading(false);
      }
    };

    if (query) {
      fetchResults();
    }
  }, [type, query]);

  const toggleSelectItem = (item) => {
    if (selectedItems.find(i => i.id === item.id)) {
      setSelectedItems(selectedItems.filter(i => i.id !== item.id));
    } else {
      setSelectedItems([...selectedItems, item]);
    }
  };

  return (
    <div className="search-results">
      <h2>Résultats pour "{query}" ({type})</h2>
      {loading ? (
        <p>Chargement...</p>
      ) : (
        <ul className="results-list">
          {results.map((item) => (
            <li
              key={item.id}
              className={selectedItems.find(i => i.id === item.id) ? "selected" : ""}
              onClick={() => toggleSelectItem(item)}
            >
              {type === 'track' ? (
                <>
                  <span className="result-title">{item.name}</span>
                  <span className="result-subtitle">{item.artists.map(a => a.name).join(', ')}</span>
                </>
              ) : (
                <>
                  <img
                    src={item.images[0]?.url}
                    alt={item.name}
                    className="result-thumbnail"
                  />
                  <span className="result-title">{item.name}</span>
                  <span className="result-subtitle">{item.release_date}</span>
                </>
              )}
            </li>
          ))}
        </ul>
      )}
      {selectedItems.length > 0 && (
        <div className="add-results-action">
          <button onClick={() => setShowModal(true)}>
            Ajouter les éléments sélectionnés à une playlist
          </button>
        </div>
      )}
      {showModal && type === 'track' && (
        <AddTracksToPlaylistModal
          selectedTracks={selectedItems}
          onClose={() => {
            setShowModal(false);
            setSelectedItems([]);
          }}
        />
      )}
      {showModal && type === 'album' && (
        <AddAlbumToPlaylistModal
          selectedAlbums={selectedItems}
          onClose={() => {
            setShowModal(false);
            setSelectedItems([]);
          }}
        />
      )}
    </div>
  );
};

export default SearchResults;
