// src/pages/PlaylistDetails.jsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getPlaylistDetails, removeTrackFromPlaylist, reorderPlaylistTracks } from '../services/spotifyService';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const PlaylistDetails = () => {
  const { id } = useParams();
  const [playlist, setPlaylist] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchPlaylistData = async () => {
    try {
      const data = await getPlaylistDetails(id);
      setPlaylist(data);
      setLoading(false);
    } catch (error) {
      console.error("Erreur lors de la récupération de la playlist:", error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPlaylistData();
  }, [id]);

  const handleRemoveTrack = async (trackUri) => {
    try {
      await removeTrackFromPlaylist(id, trackUri);
      // Recharger la playlist après suppression pour une mise à jour complète
      fetchPlaylistData();
    } catch (error) {
      console.error("Erreur lors de la suppression de la piste:", error);
      alert("Erreur lors de la suppression de la piste.");
    }
  };

  const onDragEnd = async (result) => {
    if (!result.destination) return;
    const sourceIndex = result.source.index;
    const destIndex = result.destination.index;
    if (sourceIndex === destIndex) return;

    const newTracks = Array.from(playlist.tracks.items);
    const [removed] = newTracks.splice(sourceIndex, 1);
    newTracks.splice(destIndex, 0, removed);

    setPlaylist(prev => ({
      ...prev,
      tracks: {
        ...prev.tracks,
        items: newTracks
      }
    }));

    try {
      await reorderPlaylistTracks(id, sourceIndex, destIndex);
      // Optionnel : recharger la playlist pour synchroniser avec le serveur
      // fetchPlaylistData();
    } catch (error) {
      console.error("Erreur lors du réordonnancement:", error);
      alert("Erreur lors du réordonnancement des pistes.");
    }
  };

  if (loading) return <div>Chargement...</div>;
  if (!playlist) return <div>Playlist non trouvée.</div>;

  return (
    <div className="playlist-details-container">
      <h2>{playlist.name}</h2>
      {playlist.description && <p>{playlist.description}</p>}
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="tracks-droppable">
          {(provided) => (
            <ul className="tracks-list"
                ref={provided.innerRef}
                {...provided.droppableProps}
                style={{ padding: 0, margin: 0 }}>
              {playlist.tracks.items.map((item, index) => (
                <Draggable
                  key={item.track.id || `${item.track.uri}-${index}`}
                  draggableId={item.track.id || `${item.track.uri}-${index}`}
                  index={index}
                >
                  {(provided, snapshot) => (
                    <li
                      className={`track-item ${snapshot.isDragging ? "dragging" : ""}`}
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={{
                        userSelect: "none",
                        padding: "10px",
                        marginBottom: "8px",
                        background: snapshot.isDragging ? "#e0f7fa" : "#fafafa",
                        borderRadius: "6px",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "space-between",
                        ...provided.draggableProps.style
                      }}
                    >
                      <div className="track-info" style={{ display: "flex", alignItems: "center", gap: "10px", flex: 1 }}>
                        <span className="track-number" style={{ fontWeight: "bold" }}>{index + 1}.</span>
                        <span className="track-name" style={{ flex: 1 }}>{item.track.name}</span>
                        <span className="track-duration">{formatDuration(item.track.duration_ms)}</span>
                      </div>
                      <button className="remove-track-btn" onClick={() => handleRemoveTrack(item.track.uri)} style={{
                        background: "#e74c3c",
                        border: "none",
                        padding: "6px 10px",
                        borderRadius: "4px",
                        color: "#fff",
                        cursor: "pointer"
                      }}>
                        Supprimer
                      </button>
                    </li>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </ul>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

function formatDuration(durationMs) {
  const minutes = Math.floor(durationMs / 60000);
  const seconds = ((durationMs % 60000) / 1000).toFixed(0);
  return minutes + ":" + (seconds < 10 ? '0' : '') + seconds;
}

export default PlaylistDetails;
