// src/pages/PlaylistPage.jsx
import React, { useState, useEffect } from 'react';
import { getUserPlaylists } from '../services/spotifyService';
import { useNavigate } from 'react-router-dom';
import './PlaylistPage.css';

const PlaylistPage = () => {
  const [playlists, setPlaylists] = useState([]);
  const [sortCriteria, setSortCriteria] = useState('name');
  const [sortDirection, setSortDirection] = useState('asc');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPlaylists = async () => {
      try {
        const data = await getUserPlaylists();
        setPlaylists(data);
      } catch (error) {
        console.error("Erreur lors de la récupération des playlists :", error);
      }
    };
    fetchPlaylists();
  }, []);

  const handleSort = (criteria) => {
    if (sortCriteria === criteria) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortCriteria(criteria);
      setSortDirection('asc');
    }
  };

  const sortedPlaylists = [...playlists].sort((a, b) => {
    let valueA, valueB;
    switch (sortCriteria) {
      case 'name':
        valueA = a.name.toLowerCase();
        valueB = b.name.toLowerCase();
        break;
      case 'nbChansons':
        valueA = a.tracks.total;
        valueB = b.tracks.total;
        break;
      case 'creationDate':
        valueA = new Date(a.created_at || 0);
        valueB = new Date(b.created_at || 0);
        break;
      case 'modificationDate':
        valueA = new Date(a.modified_at || 0);
        valueB = new Date(b.modified_at || 0);
        break;
      default:
        valueA = a.name.toLowerCase();
        valueB = b.name.toLowerCase();
    }
    if (valueA < valueB) return sortDirection === 'asc' ? -1 : 1;
    if (valueA > valueB) return sortDirection === 'asc' ? 1 : -1;
    return 0;
  });

  const handleRowClick = (playlistId) => {
    navigate(`/playlists/${playlistId}`);
  };

  return (
    <div className="playlist-page">
      <h1>Mes Playlists</h1>
      <table className="playlist-table">
        <thead>
          <tr>
            <th onClick={() => handleSort('name')}>
              Nom {sortCriteria === 'name' ? (sortDirection === 'asc' ? '▲' : '▼') : ''}
            </th>
            <th onClick={() => handleSort('nbChansons')}>
              Nombre de chansons {sortCriteria === 'nbChansons' ? (sortDirection === 'asc' ? '▲' : '▼') : ''}
            </th>
            <th onClick={() => handleSort('creationDate')}>
              Date de création {sortCriteria === 'creationDate' ? (sortDirection === 'asc' ? '▲' : '▼') : ''}
            </th>
            <th onClick={() => handleSort('modificationDate')}>
              Date de modification {sortCriteria === 'modificationDate' ? (sortDirection === 'asc' ? '▲' : '▼') : ''}
            </th>
          </tr>
        </thead>
        <tbody>
          {sortedPlaylists.map(pl => (
            <tr key={pl.id} onClick={() => handleRowClick(pl.id)}>
              <td>{pl.name}</td>
              <td>{pl.tracks.total}</td>
              <td>{pl.created_at ? new Date(pl.created_at).toLocaleDateString() : 'N/A'}</td>
              <td>{pl.modified_at ? new Date(pl.modified_at).toLocaleDateString() : 'N/A'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PlaylistPage;
